"""General types used by the framework."""
from typing import Union, List


# a type for general "numeric" values. i.e. an int, float, etc.
Number = Union[int, float]
# a type for "optional" numbers. i.e. either a number or None
OptionalNumber = Union[Number, None]
# a type for numeric sequences. i.e. an array, vector, etc.
Sequence = List[Number]


# explicitly define the public API of this module
__all__ = ['Number', 'OptionalNumber', 'Sequence']
